package com.example.recyclerview;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        //Preparing the dataset
        ArrayList<Company> companies = new ArrayList<>();
        Company meta = new Company("Meta", "data:image/png;base64,");
        Company google = new Company("Meta", "data:image/png;base64,");
        Company amazon = new Company("Meta", "data:image/png;base64,");
        Company apple = new Company("Meta", "data:image/png;base64,");
        Company microsoft = new Company("Meta", "data:image/png;base64,");
        Company zerodha = new Company("Meta", "data:image/png;base64,");
        Company paytm = new Company("Meta", "data:image/png;base64,");

        //Adding individual companies in arraylist
        companies.add(meta);
        companies.add(google);
        companies.add(amazon);
        companies.add(apple);
        companies.add(microsoft);
        companies.add(zerodha);
        companies.add(paytm);

        //Finding the recycler view
        RecyclerView recyclerView=findViewById(R.id.recyclerView) ;
        CompanyAdapter adapter=new CompanyAdapter(companies) ;
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));

    }
}